import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Records = () => {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [bloodGroup, setBloodGroup] = useState("");
  const [medicalHistory, setMedicalHistory] = useState("");

  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // All required fields are validated
    if (height && weight && bloodGroup) {
      // Here, you can add code to send the form data to a backend or store in state
      console.log({ height, weight, bloodGroup, medicalHistory });

      // Redirect to the dashboard after successful form submission
      navigate("/dashboard");
    } else {
      alert("Please fill in all required fields.");
    }
  };

  return (
    <div className="h-screen flex items-center justify-center ">
      <section className="w-[512px] bg-custombg p-4 flex flex-col border rounded-2xl">
        <h1 className="text-center text-white text-3xl mb-4">Records</h1>
        <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
          <div>
            <label htmlFor="height" className=" text-white font-bold text-xl">
              Height (cm):
            </label>
            <input
              type="number"
              id="height"
              value={height}
              onChange={(e) => setHeight(e.target.value)}
              required
              className="mt-1 block w-full px-3 py-2 rounded-md text-black text-lg"
            />
          </div>

          <div>
            <label htmlFor="weight" className="text-white font-bold text-xl">
              Weight (kg):
            </label>
            <input
              type="number"
              id="weight"
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
              required
              className="mt-1 block w-full px-3 py-2 rounded-md  text-black text-lg"
            />
          </div>

          <div>
            <label htmlFor="bloodGroup" className="text-white font-bold text-xl">
              Blood Group:
            </label>
            <select
              id="bloodGroup"
              value={bloodGroup}
              onChange={(e) => setBloodGroup(e.target.value)}
              required
              className="mt-1 block w-full px-3 py-2 rounded-md  text-black text-lg"
            >
              <option value="">Select Blood Group</option>
              <option value="A+">A+</option>
              <option value="A-">A-</option>
              <option value="B+">B+</option>
              <option value="B-">B-</option>
              <option value="AB+">AB+</option>
              <option value="AB-">AB-</option>
              <option value="O+">O+</option>
              <option value="O-">O-</option>
            </select>
          </div>

          <div>
            <label htmlFor="medicalHistory" className="text-black font-bold text-xl">
              Past Medical History (Optional):
            </label>
            <textarea
              id="medicalHistory"
              value={medicalHistory}
              onChange={(e) => setMedicalHistory(e.target.value)}
              className="mt-1 block w-full px-3 py-2 rounded-md text-black text-lg"
            ></textarea>
          </div>

          <button
            type="submit"
            className="w-1/2  text-white font-bold py-2 rounded-md mt-4 self-center"
          >
            Submit
          </button>
        </form>
      </section>
    </div>
  );
};

export default Records;
