import { useRef , useEffect , useState } from "react";
import {faCheck , faTimes , faInfoCircles} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

const USER_REGEX = /[a-zA-Z][a-zA-Z0-9-_]{2,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8}$/;



const Login = () => {

    const userRef = useRef();
    const errRef = useRef();

    const[user, setuser] = useState('');
    const[validname , setvalidname] = useState(false);
    const[userFocus , setuserFocus] = useState(false);

    const[pwd , setpwd] = useState('');
    const[validpwd , setvalidpwd] = useState(false);
    const[pwdFocus , setpwdFocus] = useState(false);

    const[mpwd , setmpwd] = useState('');
    const[validmpwd , setvalidmpwd] = useState(false);
    const[mpwdFocus , setmpwdFocus] = useState(false);

    const[errmsg , seterrmsg] = useState('');
    const[succmsg , setsuccmg] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    },[])

    useEffect(() => {
        const result = USER_REGEX.test(user);
        setvalidname(result)
    },[user])

    useEffect(() => {
        const result = USER_REGEX.test(user);
        setvalidpwd(result);
        const match = pwd === mpwd;
        setvalidmpwd(match);
    },[pwd , mpwd])

    useEffect(() => {
        seterrmsg('');
    },[user , pwd , mpwd]);

    handleSubmit = async (e) => {
        e.preventDefault();

        const v1 = USER_REGEX.test(user);
        const v2 = PWD_REGEX.test(pwd);
        if(!v1 || !v2) {
            seterrmsg("Invalid Entry");
            return;
        }
        setsuccmg(true)
    }

    return (
            <>
            {success ? (
                <section>
                    <h1>Success</h1>
                    <p>
                        <a href="#">Sign In</a>
                    </p>
                </section>
            ) : (
            <section>
                <p ref={errRef} className={errmsg ? "errmsg" : "offscreen"} aria-live="assertive">{errmsg}</p>
                 <h1>Sign Up</h1>
                 <form onSubmit={handleSubmit}>
                    <label htmlFor="username">
                        Username:
                        <span className={validname ? "valid" : "hide"}>
                            <FontAwesome icon={faCheck} />
                        </span>
                        <span className={validname || !user ? "hide" : "invalid"}>
                            <FontAwesome icon={faTimes} />
                        </span>

                    </label>
                    <input 
                        type="text"
                        id="username"
                        ref={userRef}
                        autoComplete="off"
                        onChange={(e) => setuser(e.target.value)}
                        required
                        aria-invalid={validname ? "false" : "true"}
                        aria-describedby="uidnote"
                        onFocus={() => setuserFocus(true)}
                        onBlur={() => setuserFocus(false)}
                    />
                    <p id="uidnote" className={userFocus && user && validname ? "instruction" : "offscreen"}>
                        Must be 4 to 24 characters.<br/>
                        No Numbers or Special Character.
                    </p>

                    <label htmlFor="password">
                        Password:
                        <span className={validpwd ? "valid" : "hide"}>
                            <FontAwesome icon={faCheck} />
                        </span>
                        <span className={validpwd || !pwd ? "hide" : "invalid"}>
                            <FontAwesome icon={faTimes} />
                        </span>

                    </label>
                    <input 
                        type="password"
                        id="password"
                        onChange={(e) => setpwd(e.target.value)}
                        required
                        aria-invalid={validpwd ? "false" : "true"}
                        aria-describedby="pwdnote"
                        onFocus={() => setpwdFocus(true)}
                        onBlur={() => setpwdFocus(false)}
                    />
                    <p id="pwdnote" className={pwdFocus && !validpwd ? "instruction" : "offscreen"}>
                        Must be 8 charchters long<br/>
                    </p>

                    <label htmlFor="confirm_pwd">
                        Confirm Password:
                        <span className={validmpwd && mpwd ? "valid" : "hide"}>
                            <FontAwesome icon={faCheck} />
                        </span>
                        <span className={validmpwd || !mpwd ? "hide" : "invalid"}>
                            <FontAwesome icon={faTimes} />
                        </span>

                    </label>
                    <input 
                        type="password"
                        id="confirm_pwd"
                        onChange={(e) => setmpwd(e.target.value)}
                        required
                        aria-invalid={validmpwd ? "false" : "true"}
                        aria-describedby="confirmnote"
                        onFocus={() => setmpwdFocus(true)}
                        onBlur={() => setmpwdFocus(false)}
                    />
                    <p id="confirm_pwd" className={mpwdFocus && !mvalidpwd ? "instruction" : "offscreen"}>
                        Must match the Password 
                    </p>

                    <button disabled={!validname || !validpwd || !validmpwd ? "false" : "true"}>Sign Up</button>
                </form>   

                <p>Already Registered</p>
                <span>
                    <a href="#">Sign In</a>
                </span>        
            </section>
            )}
        </>
    )
}

export default Login;
