import React, { useState, useEffect } from "react";

const HeroSection = () => {
  const [hospitalData, setHospitalData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState([]);

  useEffect(() => {
    
    fetch("/path/to/hospitals.json")
      .then(response => response.json())
      .then(data => {
        setHospitalData(data);
        setResults(data); 
      });
  }, []);

  const handleSearch = (event) => {
    const value = event.target.value.toLowerCase();
    setSearchTerm(value);

    const filteredResults = hospitalData.filter(hospital => 
      hospital.name.toLowerCase().includes(value) || 
      hospital.address.toLowerCase().includes(value) ||
      hospital.type.toLowerCase().includes(value)
    );

    setResults(filteredResults);
  };

  return (
    <div className="p-5 bg-custombg">
      <input
        type="text"
        className="w-full p-2 border rounded mb-4"
        placeholder="Search hospitals or clinics..."
        value={searchTerm}
        onChange={handleSearch}
      />
      
      <div>
        {results.length > 0 ? (
          results.map((hospital, index) => (
            <div key={index} className="p-4 mb-2 border rounded">
              <h2 className="text-lg font-semibold">{hospital.name}</h2>
              <p>{hospital.address}</p>
              <span className="text-sm text-gray-500">{hospital.type}</span>
            </div>
          ))
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
};

export default HeroSection;
