import { useState , useEffect } from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons';




const HospitalClinicList = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    setData(hospitalData);
  }, []);

  const renderStars = (rating) => {
    const totalStars = 5;
    const filledStars = Math.floor(rating);
    const halfStar = rating % 1 !== 0;
    
    return (
      <div className="flex">
        {[...Array(filledStars)].map((_, index) => (
          <FontAwesomeIcon key={index} icon={faStar} className="text-yellow-400" />
        ))}
        {halfStar && <FontAwesomeIcon icon={faStar} className="text-yellow-400" style={{ clipPath: "inset(0 50% 0 0)" }}/>}
        {[...Array(totalStars - filledStars - (halfStar ? 1 : 0))].map((_, index) => (
          <FontAwesomeIcon key={index + filledStars} icon={faStar} className="text-gray-400" />
        ))}
      </div>
    );
  };

  return (
    <div className="p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-white mb-4">Hospitals Nearby</h2>
        <div className="grid grid-cols-2 h-40 gap-4">
          {data
            .filter(item => item.type === "Hospital")
            .map((hospital, index) => (
              <div key={index} className="relative flex bg-custombg rounded-lg">
                <div className="w-1/3 bg-gray-300 rounded-lg">
                  <img src={hospital.image} alt={hospital.name} className="w-full h-full object-cover rounded-lg"/>
                </div>
                <div className="w-2/3 pt-4 pl-4 text-white">
                  <h3 className="font-bold text-xl">{hospital.name}</h3>
                  <p className="text-lg">{hospital.address}</p>
                  <p className="text-lg">Ph. No.: {hospital.phone}</p>
                  <div className="absolute top-2 right-2">{renderStars(hospital.rating)}</div>
                </div>
              </div>
            ))}
        </div>
      </div>

      <div className="mb-6">
        <h2 className="text-2xl text-white font-bold mb-4">Clinics Nearby</h2>
        <div className="grid grid-cols-2 h-40 gap-4">
          {data
            .filter(item => item.type === "Clinic")
            .map((clinic, index) => (
              <div key={index} className="relative flex bg-custombg rounded-lg">
                <div className="w-1/3 bg-gray-300 rounded-lg">
                  <img src={clinic.image} alt={clinic.name} className="w-full h-full object-cover rounded-lg"/>
                </div>
                <div className="w-2/3 text-white pt-4 pl-4">
                  <h3 className="font-bold text-xl">{clinic.name}</h3>
                  <p className="text-lg">{clinic.address}</p>
                  <p className="text-lg">Ph. No.: {clinic.phone}</p>
                  <div className="absolute top-2 right-2">{renderStars(clinic.rating)}</div>
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default HospitalClinicList;
