import { useRef, useEffect, useState, useContext } from "react";
import { faCheck, faTimes } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useNavigate } from "react-router-dom";
import { UserContext } from './UserContext'; // Import your UserContext

const Login = () => {
  const userRef = useRef();
  const errRef = useRef();
  const navigate = useNavigate();
  const { setUserId } = useContext(UserContext); // Destructure setUserId from context

  const [user, setUser] = useState("");
  const [validName, setValidName] = useState(false);
  const [userFocus, setUserFocus] = useState(false);
  
  const [pwd, setPwd] = useState("");
  const [validPwd, setValidPwd] = useState(false);
  const [pwdFocus, setPwdFocus] = useState(false);
  
  const [errmsg, setErrmsg] = useState("");

  useEffect(() => {
    userRef.current.focus();
  }, []);

  useEffect(() => {
    setValidName(USER_REGEX.test(user));
  }, [user]);

  useEffect(() => {
    setValidPwd(PWD_REGEX.test(pwd));
  }, [pwd]);

  useEffect(() => {
    setErrmsg("");
  }, [user, pwd]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validation checks
    if (!USER_REGEX.test(user) || !PWD_REGEX.test(pwd)) {
      setErrmsg("Invalid Entry");
      return;
    }

    try {
      const response = await fetch('http://localhost:8001/api/auth/login', { // Change to login endpoint
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: user,
          password: pwd,
        })
      });

      if (response.ok) {
        const data = await response.json(); // Assuming userId comes in the response
        localStorage.setItem('username', user);
        setUserId(data.userId); // Set userId in context
        navigate('/records'); // Navigate to records page
      } else {
        const data = await response.json();
        setErrmsg(data.message || "Login failed");
      }
    } catch (err) {
      setErrmsg("No Server Response");
    }
  };
  return (
    <div className="h-screen w-full flex flex-wrap flex-col gap-6 items-center justify-center">
      <p className="text-5xl text-custombg">DOCTIME</p>
      <section className="w-[512px] bg-custombg p-4 flex flex-col border rounded-2xl">
        <p
          ref={errRef}
          className={errmsg ? "bg-pink-100 text-red-600 font-bold p-2 mb-2" : "sr-only"}
          aria-live="assertive"
        >
          {errmsg}
        </p>
        <h1 className="text-center text-white text-3xl mb-4">Sign Up</h1>
        <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
          <div>
            <label htmlFor="username" className="text-white font-bold text-xl">
              Username:
              <span className={validName ? "text-lime-500 ml-1" : "hidden"}>
                <FontAwesomeIcon icon={faCheck} />
              </span>
              <span className={validName || !user ? "hidden" : "text-red-500 ml-1"}>
                <FontAwesomeIcon icon={faTimes} />
              </span>
            </label>
            <input
              type="text"
              id="username"
              ref={userRef}
              autoComplete="off"
              onChange={(e) => setUser(e.target.value)}
              required
              aria-invalid={validName ? "false" : "true"}
              aria-describedby="uidnote"
              className="mt-1 block w-full px-3 py-2 rounded-md bg-white text-black text-lg"
              onFocus={() => setUserFocus(true)}
              onBlur={() => setUserFocus(false)}
            />
            <p
              id="uidnote"
              className={userFocus && user && !validName ? "text-xs text-gray-200 mt-1" : "hidden"}
            >
              Must be 4 to 24 characters.<br />
              No numbers or special characters.
            </p>
          </div>

          <div>
            <label htmlFor="password" className="text-white text-xl font-bold">
              Password:
              <span className={validPwd ? "text-lime-500 ml-1" : "hidden"}>
                <FontAwesomeIcon icon={faCheck} />
              </span>
              <span className={validPwd || !pwd ? "hidden" : "text-red-500 ml-1"}>
                <FontAwesomeIcon icon={faTimes} />
              </span>
            </label>
            <input
              type="password"
              id="password"
              onChange={(e) => setPwd(e.target.value)}
              required
              aria-invalid={validPwd ? "false" : "true"}
              aria-describedby="pwdnote"
              className="mt-1 block w-full px-3 py-2 rounded-md bg-white text-black text-lg"
              onFocus={() => setPwdFocus(true)}
              onBlur={() => setPwdFocus(false)}
            />
            <p
              id="pwdnote"
              className={pwdFocus && !validPwd ? "text-xs text-gray-200 mt-1" : "hidden"}
            >
              Must be 8 characters long.<br />
              Include uppercase and lowercase letters, a number, and a special character.<br />
              Allowed special characters: ! @ # $ %
            </p>
          </div>

          <div>
            <label htmlFor="confirm_pwd" className="text-white text-xl font-bold">
              Confirm Password:
              <span className={validMpwd && mpwd ? "text-lime-500 ml-1" : "hidden"}>
                <FontAwesomeIcon icon={faCheck} />
              </span>
              <span className={validMpwd || !mpwd ? "hidden" : "text-red-500 ml-1"}>
                <FontAwesomeIcon icon={faTimes} />
              </span>
            </label>
            <input
              type="password"
              id="confirm_pwd"
              onChange={(e) => setMpwd(e.target.value)}
              required
              aria-invalid={validMpwd ? "false" : "true"}
              aria-describedby="confirmnote"
              className="mt-1 block w-full px-3 py-2 rounded-md bg-white text-black text-lg"
              onFocus={() => setMpwdFocus(true)}
              onBlur={() => setMpwdFocus(false)}
            />
            <p
              id="confirmnote"
              className={mpwdFocus && !validMpwd ? "text-xs text-gray-200 mt-1" : "hidden"}
            >
              Must match the first password input field.
            </p>
          </div>

          <button
            disabled={!validName || !validPwd || !validMpwd}
            className="w-1/4 bg-white text-custombg font-bold py-2 rounded-md mt-4 self-center
              disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Sign Up
          </button>
        </form>
        <p className="text-white mt-4 text-center font-semibold">Already Registered?</p>
        <span className="text-white text-center">
          <a href="/signin" className="underline">
            Sign In
          </a>
        </span>
      </section>
    </div>
  );
};

export default Login;
