import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Records = () => {
  const [height, setHeight] = useState("");
  const [weight, setWeight] = useState("");
  const [bloodGroup, setBloodGroup] = useState("");
  const [medicalHistory, setMedicalHistory] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (height && weight && bloodGroup) {
      try {
        // Get user ID from local storage
        const userId = localStorage.getItem("userId");

        const response = await fetch("http://localhost:8001/api/records", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            userId: userId,  // Include userId in the headers
          },
          body: JSON.stringify({ height, weight, bloodGroup, medicalHistory }),
        });

        if (response.ok) {
          console.log("Record successfully saved.");
          navigate("/dashboard"); // Navigate after successful submission
        } else {
          console.error("Error in saving the record");
        }
      } catch (error) {
        console.error("Server error:", error);
      }
    } else {
      alert("Please fill in all required fields.");
    }
  };

  return (
    <div className="h-screen flex items-center justify-center ">
      <section className="w-[512px] bg-custombg p-4 flex flex-col border rounded-2xl">
        <h1 className="text-center text-white text-3xl mb-4">Records</h1>
        <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
          <div>
            <label className="block text-white">Height (in cm):</label>
            <input
              type="number"
              value={height}
              onChange={(e) => setHeight(e.target.value)}
              required
              className="w-full p-2 border rounded"
              placeholder="Enter your height"
            />
          </div>
          <div>
            <label className="block text-white">Weight (in kg):</label>
            <input
              type="number"
              value={weight}
              onChange={(e) => setWeight(e.target.value)}
              required
              className="w-full p-2 border rounded"
              placeholder="Enter your weight"
            />
          </div>
          <div>
            <label className="block text-white">Blood Group:</label>
            <input
              type="text"
              value={bloodGroup}
              onChange={(e) => setBloodGroup(e.target.value)}
              required
              className="w-full p-2 border rounded"
              placeholder="Enter your blood group"
            />
          </div>
          <div>
            <label className="block text-white">Medical History (optional):</label>
            <textarea
              value={medicalHistory}
              onChange={(e) => setMedicalHistory(e.target.value)}
              className="w-full p-2 border rounded"
              placeholder="Enter any past medical history"
            />
          </div>
          <button
            type="submit"
            className="w-1/4 bg-white text-custombg font-bold py-2 rounded-md mt-4 self-center"
          >
            Submit
          </button>
        </form>
      </section>
    </div>
  );
};

export default Records;
