import React, { useState, useEffect } from 'react';
import Navbar from './navbar';

const Appointments = () => {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const savedAppointments = JSON.parse(localStorage.getItem('appointments')) || [];

    const validAppointments = savedAppointments.filter(appointment =>
      appointment.name &&
      appointment.doctor &&
      appointment.specialty &&
      appointment.timings &&
      appointment.slot
    );

    setAppointments(validAppointments);
  }, []);

  const handleDelete = (index) => {
    const updatedAppointments = appointments.filter((_, i) => i !== index);
    setAppointments(updatedAppointments);
    localStorage.setItem('appointments', JSON.stringify(updatedAppointments));
  };

  return (
    <div className="h-full p-8">
      <Navbar />
      <h2 className="text-white text-2xl text-center mt-6 mb-6">Scheduled Appointments</h2>
      <div className="space-y-4">
        {appointments.length > 0 ? (
          appointments.map((appointment, index) => (
            <div
              key={index}
              className="p-4 bg-custombg w-80 text-xl rounded-lg shadow-md text-white"
            >
              <p>Hospital/Clinic: {appointment.name}</p>
              <p>Doctor: {appointment.doctor}</p>
              <p>Specialty: {appointment.specialty}</p>
              <p>Timing: {appointment.timings}</p>
              <p>Slot Number: {appointment.slot}</p>
              <button
                onClick={() => handleDelete(index)}
                className="mt-2 p-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
              >
                Delete Appointment
              </button>
            </div>
          ))
        ) : (
          <p className="text-white">No appointments scheduled.</p>
        )}
      </div>
    </div>
  );
};

export default Appointments;
