import React, { useState } from 'react';

import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { AlertCircle, Home, Battery, Plane, Car, ShoppingBag, Book } from 'lucide-react';

// Mock user data
const userData = {
  household: 2,
  electricBill: 50,
  flightsPerYear: 10,
  ownsCar: false,
  commuteDistance: 1,
  usesPublicTransport: true,
  ridesharingTrips: 10,
  isVegetarian: false,
  eatsExcessiveMeat: true,
  amazonSpending: 150
};

// Helper function to calculate carbon footprint
const calculateFootprint = (data) => {
  const electricityEmission = data.electricBill * 28.037 * 12; // kgCO2 per year
  const flightEmission = data.flightsPerYear * 286.88;
  const transportationEmission = (data.commuteDistance * 0.2278 * 260) + (data.ridesharingTrips * 12 * 2.5); // Assuming 260 work days and 2.5 kg per rideshare trip
  const foodEmission = data.isVegetarian ? 1496.85482 : (data.eatsExcessiveMeat ? 2993.70964 : 2245.28223);
  const retailEmission = data.amazonSpending * 0.1289 * 12; // Yearly estimate

  return {
    electric: electricityEmission,
    flight: flightEmission,
    transportation: transportationEmission,
    food: foodEmission,
    retail: retailEmission,
    total: electricityEmission + flightEmission + transportationEmission + foodEmission + retailEmission
  };
};

const footprint = calculateFootprint(userData);

const HomeScreen = () => {
  const chartData = Object.entries(footprint).map(([key, value]) => ({ name: key, value }));
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82ca9d'];

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">EcoTrack+ Dashboard</h1>
      <Card className="mb-4">
        <CardHeader>
          <CardTitle>Your Carbon Footprint</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-3xl font-bold mb-2">{footprint.total.toFixed(2)} kg CO2/year</p>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={chartData.filter(item => item.name !== 'total')}
                cx="50%"
                cy="50%"
                labelLine={false}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {chartData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>
    </div>
  );
};

const EnergyScreen = () => {
  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Energy Consumption</h1>
      <Card className="mb-4">
        <CardHeader>
          <CardTitle>Electricity Usage</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-2xl font-bold mb-2">${userData.electricBill}/month</p>
          <p className="text-lg mb-2">{footprint.electric.toFixed(2)} kg CO2/year</p>
          <p>Your household of {userData.household} people uses approximately {(userData.electricBill / 0.14).toFixed(2)} kWh per month.</p>
        </CardContent>
      </Card>
    </div>
  );
};

const TransportationScreen = () => {
  const transportData = [
    { name: 'Commute', value: userData.commuteDistance * 0.2278 * 260 },
    { name: 'Ridesharing', value: userData.ridesharingTrips * 12 * 2.5 },
    { name: 'Flights', value: footprint.flight }
  ];

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Transportation</h1>
      <Card className="mb-4">
        <CardHeader>
          <CardTitle>Transportation Emissions</CardTitle>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={transportData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="value" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
          <p className="mt-4">You take approximately {userData.flightsPerYear} flights per year and {userData.ridesharingTrips} ridesharing trips per month.</p>
        </CardContent>
      </Card>
    </div>
  );
};

const FoodScreen = () => {
  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Food Impact</h1>
      <Card className="mb-4">
        <CardHeader>
          <CardTitle>Diet Carbon Footprint</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-2xl font-bold mb-2">{footprint.food.toFixed(2)} kg CO2/year</p>
          <p>{userData.isVegetarian ? "You follow a vegetarian diet." : (userData.eatsExcessiveMeat ? "You consume meat more than 3 times a week." : "You have a balanced diet with moderate meat consumption.")}</p>
        </CardContent>
      </Card>
    </div>
  );
};

const ShoppingScreen = () => {
  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Shopping Impact</h1>
      <Card className="mb-4">
        <CardHeader>
          <CardTitle>Retail Carbon Footprint</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-2xl font-bold mb-2">{footprint.retail.toFixed(2)} kg CO2/year</p>
          <p>You spend approximately ${userData.amazonSpending} per month on online shopping.</p>
        </CardContent>
      </Card>
    </div>
  );
};

const Login = () => {
  const [currentScreen, setCurrentScreen] = useState('home');

  const renderScreen = () => {
    switch(currentScreen) {
      case 'home': return <HomeScreen />;
      case 'energy': return <EnergyScreen />;
      case 'transportation': return <TransportationScreen />;
      case 'food': return <FoodScreen />;
      case 'shopping': return <ShoppingScreen />;
      default: return <HomeScreen />;
    }
  };

  return (
    <div className="flex flex-col h-screen">
      <div className="flex-grow overflow-auto">
        {renderScreen()}
      </div>
      <nav className="bg-gray-800 p-4">
        <ul className="flex justify-around">
          <li><button onClick={() => setCurrentScreen('home')} className="text-white"><Home /></button></li>
          <li><button onClick={() => setCurrentScreen('energy')} className="text-white"><Battery /></button></li>
          <li><button onClick={() => setCurrentScreen('transportation')} className="text-white"><Car /></button></li>
          <li><button onClick={() => setCurrentScreen('food')} className="text-white"><ShoppingBag /></button></li>
          <li><button onClick={() => setCurrentScreen('shopping')} className="text-white"><Book /></button></li>
        </ul>
      </nav>
    </div>
  );
};

export default Login;
