// src/components/records/records.jsx
import React, { useContext, useState } from 'react';
import { UserContext } from '../../context/UserContext';
import { api } from '../../api/apiconfig'; // Ensure this points to your API setup

const Records = () => {
  const { userId } = useContext(UserContext); // Destructure userId from context
  const [recordData, setRecordData] = useState({ height: '', weight: '', bloodGroup: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecordData({ ...recordData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/records', {
        userId, // Ensure you are sending userId
        ...recordData,
      });
      console.log('Record submitted successfully:', response.data);
    } catch (error) {
      console.error('Error submitting the record:', error);
    }
  };

  if (userId === null) {
    return <p className="text-red-500">No User ID available. Please log in.</p>;
  }

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-4">User Records</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="height">
            Height
          </label>
          <input
            type="text"
            name="height"
            value={recordData.height}
            onChange={handleChange}
            placeholder="Enter your height"
            required
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="weight">
            Weight
          </label>
          <input
            type="text"
            name="weight"
            value={recordData.weight}
            onChange={handleChange}
            placeholder="Enter your weight"
            required
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="bloodGroup">
            Blood Group
          </label>
          <input
            type="text"
            name="bloodGroup"
            value={recordData.bloodGroup}
            onChange={handleChange}
            placeholder="Enter your blood group"
            required
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Submit Record
        </button>
      </form>
    </div>
  );
};

export default Records;
