import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

const HospitalDetails = () => {
  const { id } = useParams(); 
  const [hospitalData, setHospitalData] = useState(null);
  const [clinicData, setClinicData] = useState(null);

  useEffect(() => {
    fetch("/doctorsdetails.json")
      .then((response) => response.json())
      .then((data) => {
        if (data.hospitals[id]) {
          setHospitalData(data.hospitals[id]);
        } else if (data.clinics[id]) {
          setClinicData(data.clinics[id]);
        }
      });
  }, [id]);

  if (!hospitalData && !clinicData) {
    return <div className="h-screen">Loading...</div>;
  }

  return (
    <div className=" h-screen text-white p-6">
      {hospitalData ? (
        <div className="cursor-pointer">
          <h2 className="text-2xl font-bold">{hospitalData.name}</h2>
          <h3 className="text-2xl text-center font-semibold mt-6">Doctors List:</h3>
          <div className="flex flex-wrap  justify-center  items-center gap-10 ">
          {hospitalData.doctors.map((doctor, index) => (
            <Link
            to={`/payment/${data.hospitals[id]}/${doctor.name}`}
            key={index}
            className="relative flex h-40 bg-custombg rounded-lg w-full sm:w-[48%] lg:w-[48%]"
          >
            <div key={index} className=" h-36 w-1/3 p-4 flex flex-col jutify-start  text-xl border rounded-lg my-4 bg-custombg shadow">
              <h3 className="font-bold text-2xl">{doctor.name}</h3>
              <p>Specialty: {doctor.specialty}</p>
              <p>Charges: ${doctor.charges}</p>
              <p>Timings: {doctor.timings}</p>
            </div>
            </Link>
          ))}
          </div>
        </div>
      ) : (
        <div>
          <h2 className="text-2xl font-bold">{clinicData.name}</h2>
          <h3 className="text-2xl font-semibold mt-4">Doctor:</h3>
          <div className="h-36 w-1/3 p-4 flex flex-col jutify-start text-xl border rounded-lg my-4 bg-custombg shadow">
            <h3 className="font-bold text-lg">{clinicData.doctor.name}</h3>
            <p>Specialty: {clinicData.doctor.specialty}</p>
            <p>Charges: ${clinicData.doctor.charges}</p>
            <p>Timings: {clinicData.doctor.timings}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default HospitalDetails;
