import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import '/hospital.json'

const HeroSection = () => {
  const [hospitalData, setHospitalData] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState([]);

  useEffect(() => {
    fetch("/hospitals.json")
      .then((response) => response.json())
      .then((data) => {
        setHospitalData(data);
        setResults(data);
        console.log(data)
      });
  }, []);

  const handleSearch = (event) => {
    const value = event.target.value.toLowerCase();
    setSearchTerm(value);

    const filteredResults = hospitalData.filter(
      (hospital) =>
        hospital.name.toLowerCase().includes(value) ||
        hospital.address.toLowerCase().includes(value) ||
        hospital.type.toLowerCase().includes(value)
    );

    setResults(filteredResults);
  };

  return (
    <div className="flex justify-evenly items-center">
      <section className="w-2/3  ml-4 my-4">
        <div className="flex items-center bg-custombg p-2 rounded-full shadow-md w-1/2">
          <input
            type="text"
            className="p-2 rounded-full flex-grow outline-none bg-white"
            placeholder="Search....."
            value={searchTerm}
            onChange={handleSearch}
          />
          <button className="w-20 bg-white p-2 rounded-full ml-2 text-gray-700 font-semibold">
            Filter
          </button>
        </div>
      </section>
      <section className="h-16 w-1/4 flex justify-evenly items-center  bg-custombg text-white text-xl rounded-xl">
            <Link to="/hospitals">Hospitals</Link>
            <p>|</p>
            <Link to="/clinic">Clinics</Link>
      </section>

      
      {searchTerm && results.length > 0 && (
        <div className="w-2/3 flex flex-col ml-4">
          {results.map((hospital, index) => (
            <div key={index} className="p-4 mb-2 border rounded-xl bg-white w-full shadow-md">
              <h2 className="text-lg font-semibold">{hospital.name}</h2>
              <p>{hospital.address}</p>
              <span className="text-sm text-gray-500">{hospital.type}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HeroSection;
