import React, { useState, useEffect } from 'react';

const DoctorDetails = ({ selectedHospitalOrClinic }) => {
  const [doctorData, setDoctorData] = useState(null);

  useEffect(() => {
    fetch('/doctors.json')
      .then((response) => response.json())
      .then((data) => {
        setDoctorData(data[selectedHospitalOrClinic]);
      });
  }, [selectedHospitalOrClinic]);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold text-white mb-4">
        {selectedHospitalOrClinic} Doctors
      </h2>
      {doctorData ? (
        Array.isArray(doctorData) ? (
          <div className="grid grid-cols-2 gap-4">
            {doctorData.map((doctor, index) => (
              <div key={index} className="flex bg-custombg rounded-lg p-4 text-white">
                <div className="w-2/3">
                  <h3 className="text-lg font-bold">{doctor.name}</h3>
                  <p>Specialty: {doctor.specialty}</p>
                  <p>Charges: ${doctor.appointmentCharges}</p>
                  <p>Timings: {doctor.timing}</p>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="flex bg-custombg rounded-lg p-4 text-white">
            <h3 className="text-lg font-bold">{doctorData.name}</h3>
            <p>Specialty: {doctorData.specialty}</p>
            <p>Charges: ${doctorData.appointmentCharges}</p>
            <p>Timings: {doctorData.timing}</p>
          </div>
        )
      ) : (
        <p>No doctor details available.</p>
      )}
    </div>
  );
};

export default DoctorDetails;
