import React, { useState, useEffect } from "react";
import { fetchHospitalData } from "../services/hospitalService";
import DoctorList from "./";

const HospitalClinicList = () => {
  const [data, setData] = useState([]);
  const [selectedHospital, setSelectedHospital] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      const hospitalData = await fetchHospitalData();
      setData(hospitalData);
    };
    loadData();
  }, []);

  const handleHospitalClick = (hospital) => {
    setSelectedHospital(hospital);
  };

  return (
    <div className="p-6">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-white mb-4">Hospitals Nearby</h2>
        <div className="grid grid-cols-2 h-40 gap-4">
          {data
            .filter(item => item.type === "Hospital")
            .map((hospital, index) => (
              <div 
                key={index} 
                className="relative flex bg-custombg rounded-lg cursor-pointer"
                onClick={() => handleHospitalClick(hospital)}
              >
                <div className="w-1/3 bg-gray-300 rounded-lg">
                  <img
                    src={hospital.image}
                    alt={hospital.name}
                    className="w-full h-full object-cover rounded-lg"
                  />
                </div>
                <div className="w-2/3 pt-4 pl-4 text-white relative">
                  <h3 className="font-bold text-xl">{hospital.name}</h3>
                  <p className="text-lg">{hospital.address}</p>
                  <p className="text-lg">Ph. No.: {hospital.phone}</p>
                  <div className="absolute bottom-2 right-2 text-sm text-yellow-400">
                    {hospital.rating} â˜…
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>

      {/* Display doctors when a hospital is clicked */}
      {selectedHospital && (
        <DoctorList doctors={selectedHospital.doctors} />
      )}
    </div>
  );
};

export default HospitalClinicList;
